import React, { Component } from 'react';
import { Map, TileLayer } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import data from '../assets/data';
import Markers from './VenueMarkers';

class MapView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      defaultLocation: { lat: 52.160114, lng: 4.497010 }, //center of Leiden
      zoom: 15,
    }
  }

  render() {
    const { longitude, latitude, zoom } = this.state;

    longitude = this.props.coords ? this.props.coords.longitude : this.state.defaultLocation.lng;
    latitude = this.props.coords ? this.props.coords.latitude : this.state.defaultLocation.lat;
    
    return (
      <Map center={latitude, longitude} zoom={zoom}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
        />
        <Markers venues={data.venues}/>
      </Map>
    );
  }
}

export default MapView;
