import React from 'react';
import { Map, TileLayer, Marker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import data from '../assets/data';
import Markers from './VenueMarkers';
import useGeolocation from '../hooks/useGeolocation';

const MapView = (props) => {

  const location = useGeolocation();

  const defaultLat = 52.160114;
  const defaultLng = 4.490900; //center of Leiden
  const zoom = 15;

  let latitude = defaultLat;
  let longitude = defaultLng;

  latitude = location.coords.lat ? latitude : defaultLat;
  longitude = location.coords.lng ? longitude : defaultLng;

  return (
    <Map center={[defaultLat, defaultLng]} zoom={zoom}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
      />
      <Markers venues={data.venues} />
      {
        location.loaded && !location.error && (
          <Marker position={[latitude, longitude]}></Marker>
        )};
    </Map>
  );
}


export default MapView;
