            room_code: "00000"
        }
    }

    toggleInGame = () => {
        this.setState({in_game: !this.state.in_game, room_code: room_value});
    };


    render() {
        const { in_game } = this.state, { setInGame } = this;
        if (!in_game) {
            return (
                <Login in_game={false} onClick={this.toggleInGame}/>
            );
        }

        else {
            return (
                <Overlay />
            );
        }
    }
}

