import React, { Component } from "react";
import Overlay from "./components/GameOverlay";
import Login from "./components/Login";

class Parent extends Component {
    constructor() {
        super();
        this.state = {
            in_game: false
        }
    }
    checkInGame = () => {
        const { in_game } = this.state;
        this.setState = {
            in_game: !in_game
        }
    }
    render() {
        const { in_game } = this.state, { change_state } = this;
        if (!in_game) {
            return (
                <Login change_state={change_state} />
            );
        }

        else {
            return (
                <Overlay />
            );
        }
    }
}

export default Parent;
