import React, { Fragment } from 'react'
import { Marker } from 'react-leaflet';
import { VenueLocationIcon } from './VenueLocationIcon';
import MarkerPopup from './MarkerPopup';

const VenueMarkers = (props) => {
  const { venues } = props;
  if(venues.length <= 0){
    return <Fragment></Fragment>;
  }

  const venue_arr = Array.from(venues);

  const markers = venues.map((venue, index) => (
    <Marker key={index} position={venue.coordinaten} icon={VenueLocationIcon} >
      <MarkerPopup data={venue} />
    </Marker>
  ));

  const marker = venue_arr.find((element) => {
    console.log(element);
    if (!element.gehaald) {
      return (
        <Marker key={element.index} position={element.coordinaten} icon={VenueLocationIcon} >
          <MarkerPopup data={element} />
        </Marker>
      );
    } 
  });
  
  
  // console.log("THIS: " + marker.name);
  return <Fragment>{marker}</Fragment>
};

export default VenueMarkers;
