import axios from "axios";
import React, { Component } from "react";

class Form extends Component {
    
    constructor(props) {
        super(props);
        this.state = { LocatieName: '', LocatieCoordinaten: '', LocatieBeschrijving: '' };
        this.handleChangeName = this.handleChangeName.bind(this);
        this.handleChangeCoords = this.handleChangeCoords.bind(this);
        this.handleChangeBeschr = this.handleChangeBeschr.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    
    handleChangeName(e){
        this.setState({
          locatieName: e.target.value
        })
      }
      handleChangeCoords(e){
        this.setState({
          locatieCoordinaten: e.target.value
        })
      }
      handleChangeBeschr(e){
        this.setState({
          locatieBeschrijving: e.target.value
        })
      }
      handleSubmit(e){
        e.preventDefault();
        const products = {
          name: this.state.locatieName,
          coordinaten: this.state.locatieCoordinaten,
          beschrijving: this.state.locatieBeschrijving
        }
        let uri = 'http://localhost:8000/api/locaties';
        axios.post(uri, products).then((response) => {
          // browserHistory.push('/display-item');
        });
      }

      render() {
        return(<form onSubmit={this.handleSubmit}>
                    <ul className="submit-objective">
                        <li>
                            <label className="runner-label">
                                <input
                                    type="text"
                                    name="beschrijving"
                                    // value={formValue.beschrijving}
                                    onChange={this.handleChangeBeschr}
                                    placeholder="Objective"
                                />
                            </label>
                            <label className="runner-label">
                                <input
                                    type="text"
                                    name="name"
                                    // value={formValue.name}
                                    onChange={this.handleChangeName}
                                    placeholder="Location"
                                />
                            </label>
                        </li>
                        <li>
                            <button type='submit'>
                                <img
                                    alt=""
                                    className="camera-icon"
                                    src="https://static.overlay-tech.com/assets/9ecae86e-1964-4d72-adf1-5e92c398c9d0.png"
                                />
                            </button>
                        </li>
                    </ul>
                </form>)
      }
}

export default Form;
