import React, { Component } from 'react';
import { Map, TileLayer, Marker } from 'react-leaflet';
import {useGeolocated} from 'react-geolocated';
import 'leaflet/dist/leaflet.css';
import data from '../assets/data';
import Markers from './VenueMarkers';

class MapView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      defaultLocation: { lat: 52.160114, lng: 4.496990 }, //center of Leiden
      zoom: 15,
    }
  }

  render() {
    let { longitude, latitude, zoom } = this.state;

    latitude = this.state.defaultLocation.lat;
    longitude = this.state.defaultLocation.lng;
    
    return (
      <Map center={[latitude, longitude]} zoom={zoom}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
        />
        <Markers venues={data.venues}/>
        {
          !this.props.coords ?
          <div className='loading'>Loading</div>
          :
          <Marker position={[latitude, longitude]}> </Marker>
        }
      </Map>
    );
  }
}


export default MapView;
